import React from 'react';

import '../components/indiaTable.scss';
import axios from 'axios'
import { Table } from 'react-bootstrap'


import ReactLoading from "react-loading";



class Display extends React.Component {


  render() { 

    if(this.props.valuE===false){
      var val= <ReactLoading type='spokes' color='grey' height={100} width={100} />
    }
    return (<div className="loading"><center>{val}</center></div>  );
  }
}



class IndiaTable extends React.Component{

  constructor(props){
    super(props)
    this.state={
      items:[],
      flag:false
    }
  }


 async componentDidMount(){
  await axios.get('http://localhost:5000')
  .then((response)=>{
    console.log(response.data.statewise)
    this.setState({items:response.data.statewise.slice(1,(response.data.statewise).length),flag:true})})
  .catch((err)=>console.log(err))






  }

  reactstrap
render(){


  return(

<div class="container">
<Display valuE={this.state.flag} />
  <h2>Responsive Tables Using LI <small>Triggers on 767px</small></h2>
  <ul class="responsive-table">
    <li class="table-header">
      <div class="col-3 col-1">STATES</div>
      <div class="col-2 col-1 col-2">CONFIRMED</div>
      <div class="col col-3">RECOVERED</div>
      <div class="col col-4">DEATHS</div>
    </li>
    {

    this.state.items.map(item=> 
    <li class="table-row">
      <div class="col-3 col-1" key={item.id}> {item.state}</div>
      <div class="col-3 col-1 col-2" key={item.id}> <large>{item.confirmed} </large><small style="color:red;">+{item.deltaconfirmed}</small></div>
      <div class="col col-3" data-label="Amount">$350</div>
      <div class="col col-4" data-label="Payment Status">Pending</div>
    </li>)}
  </ul>
    </div>




    )}


}

export default IndiaTable;
