import React from 'react';
import ReactDOM from 'react-dom';
import './index.css';
import axios from 'axios'
import { Table } from 'reactstrap'
import 'bootstrap/dist/css/bootstrap.min.css';
import Navbaro from '../src/app'
import ReactLoading from "react-loading";



class Display extends React.Component {


  render() { 

    if(this.props.valuE===false){
      var val= <ReactLoading type='spokes' color='grey' height={100} width={100} />
    }
    return (<div className="loading"><center>{val}</center></div>  );
  }
}



class App extends React.Component{

  constructor(props){
    super(props)
    this.state={
      items:[],
      flag:false
    }
  }


 async componentDidMount(){
  await axios.get('https://api.covid19india.org/data.json')
  .then((response)=>{
    console.log(response.data.statewise)
    this.setState({items:response.data.statewise.slice(1,(response.data.statewise).length),flag:true})})
  .catch((err)=>console.log(err))






  }


render(){


  return(

  <div className="tableCent">
     <Navbaro />
       <br>
       </br>
       <Display valuE={this.state.flag} />
      <Table dark>
      <thead>
        <tr>
        <th> <center>STATES</center> </th>
        <th colspan="2" scope="colgroup"><center>CONFIRMED</center></th>
         <th ><center>ACTIVE</center></th>
         <th colspan="2" scope="colgroup"><center>RECOVERED</center></th>
         <th colspan="2" scope="colgroup"><center>DEATHS</center></th>

        </tr>
      </thead>

        {

        this.state.items.map(item=> <tr> 

           <td key={item.id}> <center>{item.state}</center></td>
           <td style={{color:"yellow"}} key={item.id}> <center>{item.confirmed}</center></td>
            <td style={{color:"yellow" }}key={item.id}><center>{item.deltaconfirmed}</center></td>
            <td style={{color:"#FF0000"}} key={item.id}><center>{item.active}</center></td>
            <td style={{color:"#7CFC00"}} key={item.id}><center>{item.recovered}</center></td>
            <td style={{color:"#7CFC00"}} key={item.id}><center>{item.deltarecovered}</center></td>
            <td key={item.id}><center>{item.deaths}</center></td>
            <td key={item.id}><center>{item.deltadeaths}</center></td>

           </tr>)}



    </Table>


   </div> 
    )}


}


ReactDOM.render(
  <App />,

  document.getElementById('root')
);


